var slider=document.getElementById("slider"),beforeImg=document.querySelector(".before-img"),afterImg=document.querySelector(".after-img");function updateSlider(e){var t=e+"%";beforeImg.style.maskImage="linear-gradient(to right, transparent "+t+", black "+t+")",afterImg.style.maskImage="linear-gradient(to right, black "+t+", transparent "+t+")"}function populateSlider(){updateSlider(slider.value),slider.addEventListener("input",function(e){updateSlider(e.target.value)})}populateSlider();